import time
import os
import sys
from colorama import init, Fore, Style

init()  # Initialize colorama

def print_colored_string(s):
    for i in range(len(s)):
        if s[i] == '1':
            sys.stdout.write(Fore.WHITE + s[i])
        elif s[i] == '3':
            sys.stdout.write(Fore.YELLOW + s[i])
        elif s[i] == '5':
            sys.stdout.write(Fore.RED + s[i])
        elif s[i] == '7':
            sys.stdout.write(Fore.MAGENTA + s[i])
        elif s[i] == '9':
            sys.stdout.write(Fore.CYAN + s[i])
        else:
            sys.stdout.write(s[i])
    sys.stdout.write(Style.RESET_ALL)

def print_number_groups(numbers):
    for i in range(0, len(numbers), 5):
        group = numbers[i:i+5]
        print_colored_string(group)
        print()
        time.sleep(1.8)

numbers = ""
print_number_groups(numbers)
